
import 'package:flutter/material.dart';
import 'book.dart';
import 'book_detail_screen.dart';
import 'ajouter_livres.dart'; // Importez l'Ã©cran d'ajout de livre

class BookListScreen extends StatefulWidget {
  @override
  _BookListScreenState createState() => _BookListScreenState();
}

class _BookListScreenState extends State<BookListScreen> {
  final List<Book> books = [
    Book(title: 'Summer',
        author: 'George Orwell',
        coverUrl: 'data:image/jpeg;base64,'),
    Book(title: 'Brave New World',
        author: 'Aldous Huxley',
        coverUrl: 'data:image/jpeg;base64,'),
    Book(title: 'Fahrenheit 451',
        author: 'Ray Bradbury',
        coverUrl: 'data:image/jpeg;base64,'),
    Book(title: '1984',
        author: 'George Orwell',
        coverUrl: 'data:image/jpeg;base64,'),
    Book(title: 'The Great Gatsby',
        author: 'F. Scott Fitzgerald',
        coverUrl: 'data:image/jpeg;base64,'),
     Book(title: 'The Catcher in the Rye',
        author: 'J.D. Salinger',
        coverUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQoefFY8iSr05F-w7c-YQN4swL6LXGhm6IASw&s'),
  ];

  List<Book> filteredBooks = []; // Liste des livres filtrÃ©s
  TextEditingController searchController = TextEditingController();

  @override
  void initState() {
  super.initState();
  filteredBooks = books; // Initialisez la liste filtrÃ©e avec tous les livres au dÃ©but
  }

  @override
  Widget build(BuildContext context) {
  return Scaffold(
  appBar: AppBar(
  title: Text('Liste des livres'),
  actions: [
  IconButton(
  icon: Icon(Icons.add),
  onPressed: () {
  _navigateToAddBookScreen(); // Ouvrir l'Ã©cran d'ajout de livre
  },
  ),
  ],
  ),
  body: Column(
  children: [
  Padding(
  padding: const EdgeInsets.all(8.0),
  child: TextField(
  controller: searchController,
  decoration: InputDecoration(
  labelText: 'Rechercher par titre',
  prefixIcon: Icon(Icons.search),
  ),
  onChanged: (value) {
  _filterBooks(value); // Filtrer les livres en fonction de la recherche
  },
  ),
  ),
  Expanded(
  child: ListView.builder(
  itemCount: filteredBooks.length,
  itemBuilder: (context, index) {
  final book = filteredBooks[index];
  return GestureDetector(
  onTap: () {
  Navigator.push(
  context,
  MaterialPageRoute(
  builder: (context) => BookDetailScreen(book: book),
  ),
  );
  },
  child: Card(
  margin: EdgeInsets.symmetric(vertical: 8.0),
  shape: RoundedRectangleBorder(
  borderRadius: BorderRadius.circular(15.0),
  ),
  elevation: 5,
  child: ListTile(
  contentPadding: EdgeInsets.all(10.0),
  leading: ClipRRect(
  borderRadius: BorderRadius.circular(8.0),
  child: Image.network(
  book.coverUrl,
  width: 50,
  height: 50,
  fit: BoxFit.cover,
  ),
  ),
  title: Text(
  book.title,
  style: TextStyle(fontWeight: FontWeight.bold),
  ),
  subtitle: Text(book.author),
  trailing: Icon(Icons.arrow_forward_ios),
  ),
  ),
  );
  },
  ),
  ),
  ],
  ),
  );
  }

  // Fonction pour ouvrir l'Ã©cran d'ajout de livre
  void _navigateToAddBookScreen() {
  Navigator.push(
  context,
  MaterialPageRoute(
  builder: (context) => AddBookScreen(
  onBookAdded: (newBook) {
  setState(() {
  books.add(newBook); // Ajouter le nouveau livre Ã  la liste
  filteredBooks = books; // Mettre Ã  jour la liste filtrÃ©e
  });
  },
  ),
  ),
  );
  }

  // Fonction pour filtrer les livres en fonction de la recherche
  void _filterBooks(String query) {
  setState(() {
  filteredBooks = books.where((book) => book.title.toLowerCase().contains(query.toLowerCase())).toList();
  });
  }
}
